function renderPhpToString($filePath, $variables = array()) {\n    // Extract the variables to a local namespace\n    extract($variables);\n\n    // Start output buffering\n    ob_start();\n\n    // Include the PHP file\n    include $filePath;\n\n    // Get the content of the buffer and clean it\n    $output = ob_get_clean();\n\n    return $output;\n}\n\n// Usage\n$text = renderPhpToString('hello.php', array('foo' => 'World'));\necho $text; // Outputs: Hello, World!