$backupFile = escapeshellarg($table . "-" . date("Y-m-d-H:i:s") . '.gz');\n$dbhost = escapeshellarg($dbhost);\n$dbuser = escapeshellarg($dbuser);\n$dbpass = escapeshellarg($dbpass);\n$dbname = escapeshellarg($dbname);\n\n$command = "mysqldump --opt -h $dbhost -u $dbuser -p$dbpass $dbname | gzip > $backupFile";\n\necho $backupFile . "<br>";\n\n$output = [];\n$return_var = 0;\nexec($command . ' 2>&1', $output, $return_var);\n\necho "Return code: " . $return_var . "<br>";\necho "Output: <br>";\necho nl2br(htmlspecialchars(implode("\n", $output)));\n\nif ($return_var !== 0) {\n    echo "Error: mysqldump command failed with return code " . $return_var;\n}