public function execute()\n{\n    $lbReturn = false;\n    $lsQuery = $this->msLastQuery;\n    try\n    {\n        $mysqli = new mysqli("host", "user", "password", "database");\n\n        if ($mysqli->connect_errno) {\n            throw new MysqlException("Failed to connect to MySQL: " . $mysqli->connect_error);\n        }\n\n        $lrResource = $mysqli->query($lsQuery);\n\n        if(!$lrResource)\n        {\n            throw new MysqlException("Unable to execute query: " . $mysqli->error);\n        }\n        else\n        {\n            $this->mrQueryResource = $lrResource;\n            $lbReturn = true;\n        }\n\n        $mysqli->close();\n    }\n    catch(MysqlException $errorMsg)\n    {\n        ErrorHandler::handleException($errorMsg);\n    }\n    catch(Exception $e)\n    {\n        ErrorHandler::handleException($e);\n    }\n\n    return $lbReturn;\n}