<?php\nfunction startsWith($string, $startString) {\n    $length = strlen($startString);\n    return (substr($string, 0, $length) === $startString);\n}\n\nfunction endsWith($string, $endString) {\n    $length = strlen($endString);\n    if ($length == 0) {\n        return true;\n    }\n    return (substr($string, -$length) === $endString);\n}\n\n$str = '|apples}';\n\necho startsWith($str, '|') ? 'true' : 'false'; // Returns true\necho PHP_EOL;\necho endsWith($str, '}') ? 'true' : 'false'; // Returns true\n?>