// Using mysql_* functions (deprecated)\n$mysql_conn = mysql_connect('hostname', 'username', 'password');\nmysql_select_db('database', $mysql_conn);\n$query = mysql_query('SELECT * FROM table', $mysql_conn);\nwhile ($row = mysql_fetch_assoc($query)) {\n    echo $row['column'];\n}\nmysql_close($mysql_conn);\n\n// Using mysqli functions\n$mysqli_conn = mysqli_connect('hostname', 'username', 'password', 'database');\n$result = mysqli_query($mysqli_conn, 'SELECT * FROM table');\nwhile ($row = mysqli_fetch_assoc($result)) {\n    echo $row['column'];\n}\nmysqli_close($mysqli_conn);