<?php\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$bug_id = $_POST['bug_id'];\n$response_by = $_POST['response_by'];\n$response_content = $_POST['response_content'];\n$response_datetime = date('Y-m-d H:i:s');\n\n$sql = "INSERT INTO responses (bug_id, response_by, response_content, response_datetime) VALUES (?, ?, ?, ?)";\n\n$stmt = $conn->prepare($sql);\n$stmt->bind_param("isss", $bug_id, $response_by, $response_content, $response_datetime);\n\nif ($stmt->execute()) {\n    echo "New response added successfully";\n} else {\n    echo "Error: " . $sql . "<br>" . $conn->error;\n}\n\n$stmt->close();\n$conn->close();\n?>