class Profile {\n    const LABEL_FIRST_NAME = "First Name";\n    const LABEL_LAST_NAME = "Last Name";\n    const LABEL_COMPANY_NAME = "Company";\n}\n\n$reflection = new ReflectionClass('Profile');\n$constants = $reflection->getConstants();\n\n// To get just the constant names\n$constantNames = array_keys($constants);\n\n// To get the constant names with their values\n$constantNamesWithValues = [];\nforeach ($constants as $name => $value) {\n    $constantNamesWithValues["Profile::$name"] = $value;\n}\n\n// Output the results\nprint_r($constantNames);\nprint_r($constantNamesWithValues);