// movie_queries.php\nfunction getMovieById($mysqli, $movieId) {\n    $stmt = $mysqli->prepare("SELECT * FROM movies WHERE id = ?");\n    $stmt->bind_param("i", $movieId);\n    $stmt->execute();\n    $result = $stmt->get_result();\n    return $result->fetch_assoc();\n}\n\nfunction getActorsByMovieId($mysqli, $movieId) {\n    $stmt = $mysqli->prepare("\n        SELECT actors.* FROM actors\n        JOIN movie_actors ON actors.id = movie_actors.actor_id\n        WHERE movie_actors.movie_id = ?");\n    $stmt->bind_param("i", $movieId);\n    $stmt->execute();\n    return $stmt->get_result()->fetch_all(MYSQLI_ASSOC);\n}