$Router = $this->_front->getRouter();\n\n// Define the route with defaults\n$CategoryRoute = new Zend_Controller_Router_Route(\n    'category/:category/:controller/:action/*',\n    array(\n        'controller' => 'index', // Default controller\n        'action' => 'index',     // Default action\n        'category' => 'default'  // Default category\n    )\n);\n\n// Add the route to the router\n$Router->addRoute('category', $CategoryRoute);