<?php\n// Database connection\n$pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n\n// Get the current date and set the scheduled time to 6:00 PM\n$scheduled_time = new DateTime();\n$scheduled_time->setTime(18, 0);\n\n// Prepare the SQL statement\n$stmt = $pdo->prepare("INSERT INTO email_queue (recipient, subject, body, attachments, scheduled_time, status) VALUES (?, ?, ?, ?, ?, 'pending')");\n\n// Loop through selected recipients and queue the emails\nforeach ($selected_recipients as $recipient) {\n    $stmt->execute([$recipient['email'], $subject, $body, json_encode($attachments), $scheduled_time->format('Y-m-d H:i:s')]);\n}\n?>