// Add a thumbnail field to category edit form\nfunction add_category_thumbnail_field($taxonomy) {\n    ?>\n    <div class="form-field term-thumbnail-wrap">\n        <label for="term-thumbnail"><?php _e('Category Thumbnail', 'textdomain'); ?></label>\n        <input type="text" name="term-thumbnail" id="term-thumbnail" value="" class="term-thumbnail-field" />\n        <p><?php _e('Enter the URL of the category thumbnail.', 'textdomain'); ?></p>\n    </div>\n    <?php\n}\nadd_action('category_add_form_fields', 'add_category_thumbnail_field');\nadd_action('category_edit_form_fields', 'add_category_thumbnail_field');\n\n// Save the category thumbnail meta\nfunction save_category_thumbnail($term_id) {\n    if (isset($_POST['term-thumbnail'])) {\n        update_term_meta($term_id, 'category_thumbnail', esc_url($_POST['term-thumbnail']));\n    }\n}\nadd_action('created_category', 'save_category_thumbnail');\nadd_action('edited_category', 'save_category_thumbnail');