class PlzComplicateMyLife {\n    public $vehicle;\n    public $kindVehicle;\n    public $dynamicFunction;\n\n    public function __construct($vehicle, $kindVehicle) {\n        $this->kindVehicle = $kindVehicle;\n        $this->vehicle = $vehicle;\n\n        switch($kindVehicle) {\n            case 'cycle':\n                $this->dynamicFunction = "isACycle";\n                break;\n            case 'car':\n                $this->dynamicFunction = "isACar";\n                break;\n        }\n    }\n\n    public function isACycle($vehicle) {\n        echo 'I am a cycle, model: ' . $vehicle . '<br />';\n    }\n\n    public function isACar($vehicle) {\n        echo 'I am a car, model: ' . $vehicle . '<br />';\n    }\n\n    public function printKind() {\n        // Call the dynamic function\n        call_user_func([$this, $this->dynamicFunction], $this->vehicle);\n    }\n}\n\n$maserati = new PlzComplicateMyLife('maserati4', 'car');\n$ducati = new PlzComplicateMyLife('ducati', 'cycle');\n\n// Call the printKind method to demonstrate the dynamic function call\n$maserati->printKind();\n$ducati->printKind();