<?php\n// get_bookmarks.php\n\n// Database connection (adjust parameters accordingly)\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Fetch bookmarks data\n$sql = "SELECT id, title, url FROM bookmarks";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    echo "<table id='bookmarks'>";\n    echo "<tr><th>ID</th><th>Title</th><th>URL</th></tr>";\n    while($row = $result->fetch_assoc()) {\n        echo "<tr><td>" . $row["id"]. "</td><td>" . $row["title"]. "</td><td>" . $row["url"]. "</td></tr>";\n    }\n    echo "</table>";\n} else {\n    echo "0 results";\n}\n$conn->close();\n?>