function format_postcode($postcode) {\n    // Remove any existing spaces\n    $postcode = preg_replace('/\s+/', '', $postcode);\n\n    // Use a regular expression to split the postcode into its parts\n    if (preg_match('/^([A-Z]{1,2}[0-9][0-9A-Z]?)([0-9][A-Z]{2})$/i', $postcode, $matches)) {\n        // Reformat the postcode with a space\n        return strtoupper($matches[1] . ' ' . $matches[2]);\n    } else {\n        // Invalid postcode format\n        return false;\n    }\n}\n\n// Example usage\n$input_postcode = "W14SB";\n$formatted_postcode = format_postcode($input_postcode);\n\nif ($formatted_postcode !== false) {\n    echo "Formatted Postcode: " . $formatted_postcode;\n} else {\n    echo "Invalid Postcode";\n}