function getYouTubeThumbnail($url, $size = 'big') {\n    preg_match('/[\\?\\&]v=([^\\?\\&]+)/', $url, $matches);\n    $videoId = isset($matches[1]) ? $matches[1] : $url;\n\n    if ($size === 'small') {\n        return "http://img.youtube.com/vi/$videoId/2.jpg";\n    } else {\n        return "http://img.youtube.com/vi/$videoId/0.jpg";\n    }\n}\n\n// Usage\n$imgUrl_big = getYouTubeThumbnail("https://www.youtube.com/watch?v=uVLQhRiEXZs");\n$imgUrl_small = getYouTubeThumbnail("https://www.youtube.com/watch?v=uVLQhRiEXZs", 'small');