class ModuleController extends CI_Controller {\n\n    public function index() {\n        // Load the module model\n        $this->load->model('module_model');\n\n        // Get the modules selected by the user\n        $modules = $this->module_model->getModuleUser();\n\n        foreach ($modules as $module) {\n            // Get the module name\n            $module_name = $module->mod_name;\n\n            // Dynamically load the model for the module\n            $model_name = $module_name . '_model';\n            $this->load->model($model_name);\n\n            // Dynamically call the model's method to get data\n            $data = $this->$model_name->get();\n\n            // Load the view and pass the data\n            $this->load->view($module_name . '_view', $data);\n        }\n    }\n}