<?php\n$list = array('page1', 'page2', 'page3');\n\n// Function to set array pointer to a specific position\nfunction setPointerToPosition(&$array, $position) {\n    reset($array); // Set the pointer to the beginning of the array\n    for ($i = 0; $i < $position; $i++) {\n        next($array); // Advance the pointer to the desired position\n    }\n}\n\n// Example usage\n$currentPage = 'page2';\n$currentPosition = array_search($currentPage, $list);\n\nif ($currentPosition !== false) {\n    setPointerToPosition($list, $currentPosition);\n    \n    // Now you can use next() and prev() relative to the current position\n    echo 'Current: ' . current($list) . "\n"; // Should output 'page2'\n\n    // Next page\n    $nextPage = next($list);\n    echo 'Next: ' . $nextPage . "\n"; // Should output 'page3'\n\n    // Previous page (resetting pointer back to original position first)\n    setPointerToPosition($list, $currentPosition);\n    $prevPage = prev($list);\n    echo 'Previous: ' . $prevPage . "\n"; // Should output 'page1'\n} else {\n    echo "Page not found in the list.\n";\n}\n?>