<?php\n// Generate a key and initialization vector (IV)\n$key = openssl_random_pseudo_bytes(32);\n$iv = openssl_random_pseudo_bytes(openssl_cipher_iv_length('aes-256-cbc'));\n\n// HTML content to encrypt\n$htmlContent = '<form>...</form>';\n\n// Encrypt the content\n$encryptedData = openssl_encrypt($htmlContent, 'aes-256-cbc', $key, 0, $iv);\n\n// Base64 encode the encrypted data, key, and IV to safely transmit over HTTP\n$encryptedDataBase64 = base64_encode($encryptedData);\n$keyBase64 = base64_encode($key);\n$ivBase64 = base64_encode($iv);\n?>\n\n<script>\n// Pass the encrypted data, key, and IV to JavaScript\nvar encryptedData = "<?php echo $encryptedDataBase64; ?>";\nvar key = "<?php echo $keyBase64; ?>";\nvar iv = "<?php echo $ivBase64; ?>";\n</script>