<?php\n// Database connection\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Prepare the SQL statement\n$stmt = $mysqli->prepare("INSERT INTO your_table (date_column, time_column) VALUES (?, ?)");\n\n// Check if the statement was prepared successfully\nif ($stmt === false) {\n    die("Prepare failed: " . $mysqli->error);\n}\n\n// Example date and time values\n$date = '2023-10-15'; // YYYY-MM-DD format for DATE column\n$time = '14:30:00';   // HH:MM:SS format for TIME column\n\n// Bind the parameters (s = string)\n$stmt->bind_param("ss", $date, $time);\n\n// Execute the statement\nif ($stmt->execute()) {\n    echo "Record inserted successfully";\n} else {\n    echo "Error: " . $stmt->error;\n}\n\n// Close the statement and the connection\n$stmt->close();\n$mysqli->close();\n?>