function addWww($url) {\n    $parsedUrl = parse_url($url);\n\n    // Check if the host does not start with 'www.'\n    if (isset($parsedUrl['host']) && strpos($parsedUrl['host'], 'www.') !== 0) {\n        $parsedUrl['host'] = 'www.' . $parsedUrl['host'];\n    }\n\n    // Rebuild the URL\n    $newUrl = (isset($parsedUrl['scheme']) ? $parsedUrl['scheme'] . '://' : '') .\n              $parsedUrl['host'] .\n              (isset($parsedUrl['path']) ? $parsedUrl['path'] : '') .\n              (isset($parsedUrl['query']) ? '?' . $parsedUrl['query'] : '') .\n              (isset($parsedUrl['fragment']) ? '#' . $parsedUrl['fragment'] : '');\n\n    return $newUrl;\n}\n\n// Example usage:\n$url = "http://imdb.com/whatever";\n$normalizedUrl = addWww($url);\n\necho $normalizedUrl; // Outputs: http://www.imdb.com/whatever