function formatNumber($number) {\n    if ($number >= 1000000000) {\n        return number_format($number / 1000000000, 1) . ' Bil';\n    } elseif ($number >= 1000000) {\n        return number_format($number / 1000000, 1) . ' Mil';\n    } elseif ($number >= 1000) {\n        return number_format($number / 1000, 1) . ' K';\n    } else {\n        return $number;\n    }\n}\n\n// Examples\necho formatNumber(100500000); // Output: 100.5 Mil\necho formatNumber(200400);    // Output: 200.4 K\necho formatNumber(143000000); // Output: 143 Mil\necho formatNumber(52000000000); // Output: 52 Bil