<?php\n// Database connection\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// The query to find records in Table1 that are not linked to a specific parent in Table2\n$accountID = 1; // Example accountID\n$sql = "\n    SELECT table1.ID, table1.Name, table1.Country\n    FROM table1\n    LEFT JOIN table2 ON table1.ID = table2.accountID AND table2.accountID = ?\n    WHERE table2.accountID IS NULL\n";\n\n$stmt = $conn->prepare($sql);\n$stmt->bind_param("i", $accountID);\n$stmt->execute();\n$result = $stmt->get_result();\n\n// Fetch and display results\nwhile ($row = $result->fetch_assoc()) {\n    echo "ID: " . $row["ID"]. " - Name: " . $row["Name"]. " - Country: " . $row["Country"]. "<br>";\n}\n\n$stmt->close();\n$conn->close();\n?>