// Assuming you have a model class for your users table\nclass Users extends Zend_Db_Table_Abstract\n{\n    protected $_name = 'users'; // Name of your table\n}\n\n// Controller action or wherever you handle the form\npublic function editUserAction()\n{\n    // Get the user ID from the request (e.g., URL parameter)\n    $userId = $this->_getParam('id');\n\n    // Fetch the user data\n    $usersTable = new Users();\n    $userRow = $usersTable->fetchRow($usersTable->select()->where('id = ?', $userId));\n\n    if ($userRow) {\n        // Convert the row to an array\n        $userData = $userRow->toArray();\n\n        // Create an instance of the form\n        $form = new UsersForm();\n\n        // Populate the form with the user data\n        $form->populate($userData);\n\n        // Pass the form to the view\n        $this->view->form = $form;\n    } else {\n        // Handle the case where the user is not found\n        throw new Zend_Controller_Action_Exception('User not found', 404);\n    }\n}