<?php\nrequire_once 'path/to/facebook-php-sdk/autoload.php'; // Update with the correct path to the Facebook PHP SDK\n\nuse Facebook\Facebook;\n\n$accessToken = $_REQUEST['token'];\n\n$fb = new Facebook([\n  'app_id' => 'your-app-id', // Replace with your app ID\n  'app_secret' => 'your-app-secret', // Replace with your app secret\n  'default_graph_version' => 'v10.0',\n]);\n\ntry {\n  $response = $fb->get('/me/friends', $accessToken);\n  $friends = $response->getDecodedBody();\n  echo json_encode($friends);\n} catch(Facebook\Exceptions\FacebookResponseException $e) {\n  echo 'Graph returned an error: ' . $e->getMessage();\n  exit;\n} catch(Facebook\Exceptions\FacebookSDKException $e) {\n  echo 'Facebook SDK returned an error: ' . $e->getMessage();\n  exit;\n}\n?>