add_action('template_redirect', 'custom_template_redirect');\n   function custom_template_redirect() {\n       $custom_var = get_query_var('custom_var');\n       $custom_var2 = get_query_var('custom_var2');\n\n       if ($custom_var) {\n           // Handle the URL dynamically here\n           // For example, load a custom template or execute specific code\n           echo "Handling custom URL: /foo/$custom_var";\n           if ($custom_var2) {\n               echo "/$custom_var2";\n           }\n           exit;\n       }\n   }