<?php\n// Assuming you have the timezone and dates\n$timezone = 'America/New_York'; // Replace with your timezone\n\n// Create DateTime objects\n$now = new DateTime('now', new DateTimeZone($timezone));\n$eob = new DateTime('2009-07-02 17:00:00', new DateTimeZone($timezone));\n\n// Calculate the difference\n$interval = $now->diff($eob);\n\n// Get the difference in seconds\n$timediff = ($interval->days * 24 * 60 * 60) + ($interval->h * 60 * 60) + ($interval->i * 60) + $interval->s;\n\necho "Time difference in seconds: " . $timediff;\n?>