require 'vendor/autoload.php';\n\n$apiKey = 'YOUR_API_KEY'; // Replace with your YouTube Data API key\n$videoId = 'VIDEO_ID'; // Replace with the YouTube video ID\n\n$client = new Google_Client();\n$client->setDeveloperKey($apiKey);\n\n$youtube = new Google_Service_YouTube($client);\n\n$comments = [];\n$pageToken = null;\n\ndo {\n    $params = [\n        'videoId' => $videoId,\n        'part' => 'snippet',\n        'maxResults' => 100, // Maximum number of results per page\n        'pageToken' => $pageToken\n    ];\n\n    $response = $youtube->commentThreads->listCommentThreads('snippet', $params);\n\n    foreach ($response['items'] as $item) {\n        $comments[] = $item['snippet']['topLevelComment']['snippet']['textDisplay'];\n    }\n\n    $pageToken = $response['nextPageToken'];\n} while ($pageToken);\n\nforeach ($comments as $comment) {\n    echo $comment . PHP_EOL;\n}