// Using Zend_Filter_Input for sanitization and validation\n$filters = array(\n    '*' => 'StringTrim', // Apply StringTrim filter to all inputs\n);\n\n$validators = array(\n    'input_key' => 'NotEmpty', // Validate that 'input_key' is not empty\n);\n\n$input = new Zend_Filter_Input($filters, $validators, $this->_request->getPost());\nif ($input->isValid()) {\n    $inputData = $input->input_key;\n    // Process the valid input data\n} else {\n    $messages = $input->getMessages();\n    foreach ($messages as $message) {\n        echo "Validation failed: $message\n";\n    }\n}