<?php\n   // Sample PHP script to query the database and return JSON-encoded data\n   header('Content-Type: application/json');\n\n   // Database connection (replace with your connection details)\n   $mysqli = new mysqli("localhost", "username", "password", "database");\n\n   if ($mysqli->connect_error) {\n       die("Connection failed: " . $mysqli->connect_error);\n   }\n\n   // Query the database (replace with your actual query)\n   $result = $mysqli->query("SELECT id, title FROM your_table");\n\n   $rows = array();\n   while ($row = $result->fetch_assoc()) {\n       $rows[] = $row;\n   }\n\n   // Encode the result as JSON and output it\n   echo json_encode($rows);\n\n   $mysqli->close();\n   ?>