class DataImporter {\n    private $fetcher;\n    private $handler;\n    private $writer;\n\n    public function __construct(DataFetcherInterface $fetcher, DataHandlerInterface $handler, DataWriterInterface $writer) {\n        $this->fetcher = $fetcher;\n        $this->handler = $handler;\n        $this->writer = $writer;\n    }\n\n    public function import(): void {\n        try {\n            $data = $this->fetcher->fetch();\n            $handledData = $this->handler->handle($data);\n            $this->writer->write($handledData);\n        } catch (Exception $e) {\n            ErrorHandler::handle($e);\n        }\n    }\n}