<?php\n$a = 5; // integer\n$b = "5"; // string\n\nif ($a != $b) {\n    echo '$a is not equal to $b';\n} else {\n    echo '$a is equal to $b'; // This will be the output, because the values are considered equal\n}\n\nif ($a !== $b) {\n    echo '$a is not identical to $b'; // This will be the output, because the types are different\n} else {\n    echo '$a is identical to $b';\n}\n?>