<?php\nfunction getFirstSentence($text) {\n    // Regular expression to capture the first sentence\n    if (preg_match('/(.*?[\.\!\?])\s/', $text, $matches)) {\n        return $matches[1];\n    }\n    // Return the whole text if no sentence-ending punctuation is found\n    return $text;\n}\n\n$description = 'We prefer questions that can be answered, not just discussed. Provide details. Write clearly and simply.';\n$firstSentence = getFirstSentence($description);\n\necho $firstSentence; // Outputs: We prefer questions that can be answered, not just discussed.\n?>