if ($sscount > 0) {\n    for ($i = 1; $i <= $sscount; $i++) {\n        $file = $_FILES["image$i"];\n        $fileType = $file["type"];\n        $fileSize = $file["size"];\n        $fileTmpName = $file["tmp_name"];\n        $fileName = basename($file["name"]); // Use basename to avoid directory traversal\n\n        if (in_array($fileType, $allowedTypes) && $fileSize < 500000) {\n            // Sanitize file name\n            $sanitizedFileName = preg_replace("/[^a-zA-Z0-9_\-\.]/", "", $fileName);\n            $destination = "uploads/" . $sanitizedFileName;\n\n            // Move the file to the desired location\n            if (move_uploaded_file($fileTmpName, $destination)) {\n                echo "File $i uploaded successfully.<br />";\n            } else {\n                echo "Error: Unable to upload image $i.<br />";\n            }\n        } else {\n            $errormsg .= "Error: Image $i must be either JPEG, GIF, or PNG and less than 500 kb.<br />";\n        }\n    }\n}