class Node {\n    public $left;\n    public $right;\n    public $children = [];\n\n    public function __construct() {\n        $this->children = [];\n    }\n}\n\nfunction calculateNestedSetValues($node, &$index) {\n    $node->left = ++$index;\n\n    foreach ($node->children as $child) {\n        calculateNestedSetValues($child, $index);\n    }\n\n    $node->right = ++$index;\n}\n\n// Example usage\n$root = new Node();\n$child1 = new Node();\n$child2 = new Node();\n$root->children = [$child1, $child2];\n\n$index = 0;\ncalculateNestedSetValues($root, $index);\n\n// Output the results\necho "Root: Left = $root->left, Right = $root->right\n";\necho "Child1: Left = $child1->left, Right = $child1->right\n";\necho "Child2: Left = $child2->left, Right = $child2->right\n";