class Example {\n    var $oldProperty; // Old-style class attribute declaration\n    public $newProperty; // Modern public class attribute declaration\n\n    public function __construct() {\n        $this->oldProperty = "Old Value";\n        $this->newProperty = "New Value";\n    }\n\n    public function displayProperties() {\n        echo $this->oldProperty . "\n";\n        echo $this->newProperty . "\n";\n    }\n}\n\n$example = new Example();\n$example->displayProperties();\n\n// Regular variable\n$foo = "Hello, World!";\necho $foo;