function sqlLikeMatch($pattern, $subject) {\n    // Convert SQL LIKE pattern to a regex pattern\n    $pattern = str_replace(['%', '_'], ['.*', '.'], $pattern);\n    $pattern = "/^" . $pattern . "$/";\n\n    // Perform the match using preg_match\n    return preg_match($pattern, $subject) === 1;\n}\n\n// Example usage\n$keyword = 'matchme%';\n$stringToMatch = 'matchmeanything';\n\nif (sqlLikeMatch($keyword, $stringToMatch)) {\n    echo "Match found!";\n} else {\n    echo "No match found.";\n}