<?php\n// Capture the start time\n$start = getrusage();\n\n// Your script logic here\n// ...\n\n// Capture the end time\n$end = getrusage();\n\n// Function to calculate the time difference\nfunction getCpuUsage($ru, $rus, $index) {\n    return ($ru["ru_$index.tv_sec"] * 1000 + intval($ru["ru_$index.tv_usec"] / 1000))\n         - ($rus["ru_$index.tv_sec"] * 1000 + intval($rus["ru_$index.tv_usec"] / 1000));\n}\n\n$user_time = getCpuUsage($end, $start, "utime"); // User CPU time used\n$system_time = getCpuUsage($end, $start, "stime"); // System CPU time used\n\necho "User CPU time: " . $user_time . " ms\n";\necho "System CPU time: " . $system_time . " ms\n";\n?>