function hasMismatchedParentheses($string) {\n    $stack = array();\n    $length = strlen($string);\n\n    for ($i = 0; $i < $length; $i++) {\n        if ($string[$i] == '(') {\n            array_push($stack, '(');\n        } elseif ($string[$i] == ')') {\n            if (empty($stack)) {\n                return true; // Mismatched parenthesis found\n            }\n            array_pop($stack);\n        }\n    }\n\n    return !empty($stack); // If stack is not empty, parentheses are mismatched\n}\n\n// Test cases\n$testStrings = array(\n    "This is (ok)",\n    "This (is) (ok)",\n    "This is )bad(",\n    "This is also (bad",\n    "This is (bad (too)"\n);\n\nforeach ($testStrings as $string) {\n    if (hasMismatchedParentheses($string)) {\n        echo "Mismatched parentheses in: \"$string\"\n";\n    } else {\n        echo "Balanced parentheses in: \"$string\"\n";\n    }\n}