<?php\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    $left = $_POST['left'];\n    $top = $_POST['top'];\n    \n    // Save $left and $top to the database\n    // This is just an example, make sure to use prepared statements to prevent SQL injection\n\n    // Example database connection and query\n    $conn = new mysqli('hostname', 'username', 'password', 'database');\n    if ($conn->connect_error) {\n        die('Connection failed: ' . $conn->connect_error);\n    }\n\n    $stmt = $conn->prepare("UPDATE positions SET left = ?, top = ? WHERE element_id = ?");\n    $stmt->bind_param('ddi', $left, $top, $element_id);\n    $element_id = 1; // Example element ID\n    if ($stmt->execute()) {\n        echo "Position saved successfully.";\n    } else {\n        echo "Error saving position.";\n    }\n\n    $stmt->close();\n    $conn->close();\n}\n?>