// Example query to retrieve article, author, comments, and comment authors in a single query\n$articleId = (int)$_GET['article_id'];\n$sql = "\n    SELECT \n        articles.content,\n        articles.info,\n        IFNULL(users.name, 'Guest') AS author_name,\n        comments.content AS comment_content,\n        comment_authors.name AS comment_author_name\n    FROM articles\n    LEFT JOIN users ON articles.author_id = users.id\n    LEFT JOIN comments ON comments.article_id = articles.id\n    LEFT JOIN users AS comment_authors ON comments.author_id = comment_authors.id\n    WHERE articles.id = $articleId\n";\n\n// Execute the query\n$result = mysqli_query($conn, $sql);\n\n// Process the results\nwhile ($row = mysqli_fetch_assoc($result)) {\n    // Handle the data\n}\n\n// Update the view counter separately if needed\n$updateSql = "UPDATE articles SET view_counter = view_counter + 1 WHERE id = $articleId";\nmysqli_query($conn, $updateSql);