// Function to safely display user input in HTML\nfunction escapeOutput($string)\n{\n    return htmlspecialchars($string, ENT_QUOTES, 'UTF-8');\n}\n\n// Example Usage\n$username = escapeOutput($userInput); // Safe to display in HTML\n\n// Using parameterized queries for SQL\n$mysqli = new mysqli("localhost", "user", "password", "database");\n$stmt = $mysqli->prepare("SELECT * FROM users WHERE username = ?");\n$stmt->bind_param("s", $usernameFromForm); // $usernameFromForm should be the raw user input\n$stmt->execute();\n$result = $stmt->get_result();\nwhile ($row = $result->fetch_assoc()) {\n    // Process the result safely\n    echo escapeOutput($row['username']); // Example of output encoding\n}\n$stmt->close();\n$mysqli->close();