<?php\n$host = "host";\n$user = "user";\n$password = "password";\n$database = "somedb";\n$newUser = "someuser";\n$newPassword = "somepass";\n$sqlFilePath = "path/to/sqlfile.sql";\n\n// Connect to MySQL\n$mysqli = new mysqli($host, $user, $password);\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Create database\nif (!$mysqli->query("CREATE DATABASE IF NOT EXISTS `$database`")) {\n    die("Database creation failed: " . $mysqli->error);\n}\n\n// Select the database\nif (!$mysqli->select_db($database)) {\n    die("Database selection failed: " . $mysqli->error);\n}\n\n// Create user and grant privileges\nif (!$mysqli->query("CREATE USER '$newUser'@'localhost' IDENTIFIED BY '$newPassword'")) {\n    die("User creation failed: " . $mysqli->error);\n}\nif (!$mysqli->query("GRANT ALL PRIVILEGES ON `$database`.* TO '$newUser'@'localhost'")) {\n    die("Granting privileges failed: " . $mysqli->error);\n}\n\n// Import SQL file\n$sql = file_get_contents($sqlFilePath);\nif ($sql === false) {\n    die("Failed to read SQL file");\n}\n$queries = explode(';', $sql);\nforeach ($queries as $query) {\n    $trimmedQuery = trim($query);\n    if (!empty($trimmedQuery)) {\n        if (!$mysqli->query($trimmedQuery)) {\n            die("Error executing query: " . $mysqli->error);\n        }\n    }\n}\n\n// Close connection\n$mysqli->close();\n\necho "Database and user created successfully, and SQL file imported.";\n?>