<?php\n// Example with addslashes()\n$user_input = "O'Reilly";\n$safe_input = addslashes($user_input);\n$query = "SELECT * FROM users WHERE name = '$safe_input'";\n// Resulting query: SELECT * FROM users WHERE name = 'O\'Reilly'\n// This might work but is not reliable across different database systems and encodings\n\n// Example with mysql_real_escape_string()\n$link = mysqli_connect("localhost", "username", "password", "database");\n$user_input = "O'Reilly";\n$safe_input = mysqli_real_escape_string($link, $user_input);\n$query = "SELECT * FROM users WHERE name = '$safe_input'";\n// Resulting query: SELECT * FROM users WHERE name = 'O\'Reilly'\n// Properly escaped considering MySQL's specific context and character set\n?>