global $wpdb;\n$user_id = mysql_insert_id();\n\n$meta_values = array(\n    array('meta_key' => 'nickname', 'meta_value' => $email),\n    array('meta_key' => 'rich_editing', 'meta_value' => 'true'),\n    array('meta_key' => 'comment_shortcuts', 'meta_value' => 'false'),\n    array('meta_key' => 'admin_color', 'meta_value' => 'fresh'),\n    array('meta_key' => 'wp_capabilities', 'meta_value' => 'a:1:{s:10:"subscriber";b:1;}')\n);\n\nforeach ($meta_values as $meta) {\n    $wpdb->insert(\n        $wpdb->usermeta,\n        array(\n            'user_id' => $user_id,\n            'meta_key' => $meta['meta_key'],\n            'meta_value' => $meta['meta_value']\n        ),\n        array(\n            '%d',\n            '%s',\n            '%s'\n        )\n    );\n}