$js = "\n   window.addEvent('domready', function() {\n       // Function to load the remote script\n       function loadScript(url, callback) {\n           var script = document.createElement('script');\n           script.type = 'text/javascript';\n           script.src = url;\n           \n           script.onload = function() {\n               callback();\n               // Remove the script after loading\n               document.head.removeChild(script);\n           };\n           \n           document.head.appendChild(script);\n       }\n       \n       // Load the remote JS Library\n       loadScript('https://example.com/remote-library.js', function() {\n           // Execute your code that depends on the remote library\n           console.log('Remote library loaded and executed.');\n       });\n   });\n   ";\n   $document->addScriptDeclaration($js);