function create_signed_cookie($name, $value, $secret_key) {\n        $signature = hash_hmac('sha256', $value, $secret_key);\n        setcookie($name, $value . '|' . $signature, time() + 3600, '/', '', true, true);\n    }\n\n    function verify_signed_cookie($name, $secret_key) {\n        if (!isset($_COOKIE[$name])) {\n            return false;\n        }\n\n        list($value, $signature) = explode('|', $_COOKIE[$name], 2);\n\n        $expected_signature = hash_hmac('sha256', $value, $secret_key);\n        if (hash_equals($signature, $expected_signature)) {\n            return $value;\n        } else {\n            return false;\n        }\n    }