<?php\nrequire 'vendor/autoload.php'; // Autoload SimplePie using Composer\n\n$feed = new SimplePie();\n$feed->set_feed_url('https://example.com/feed'); // URL of the RSS or ATOM feed\n$feed->enable_cache(false); // Disable caching for simplicity\n$feed->init();\n$feed->handle_content_type();\n\nif ($feed->error()) {\n    echo 'Error: ' . $feed->error();\n} else {\n    foreach ($feed->get_items() as $item) {\n        echo '<h2>' . $item->get_title() . '</h2>';\n        echo '<p>' . $item->get_date('j F Y | g:i a') . '</p>';\n        echo '<p>' . $item->get_content() . '</p>';\n    }\n}\n\n// Check if a URL is a valid feed\nfunction is_feed($url) {\n    $simplepie = new SimplePie();\n    $simplepie->set_feed_url($url);\n    $simplepie->enable_cache(false);\n    $simplepie->init();\n    return !$simplepie->error();\n}\n\n// Example usage\n$url = 'https://example.com/feed';\nif (is_feed($url)) {\n    echo "$url is a valid feed.";\n} else {\n    echo "$url is not a valid feed.";\n}\n?>