class myClass {\n    public static function test1() {\n        if (method_exists("myClass", "test1")) {\n            echo "Hi";\n        }\n    }\n\n    public function test2() {\n        if (method_exists($this, "test2")) {\n            echo "Hi";\n        }\n    }\n\n    public static function test3() {\n        // Use __CLASS__ to refer to the current class\n        if (method_exists(__CLASS__, "test3")) {\n            echo "Hi";\n        }\n    }\n}\n\n// Echos Hi\nmyClass::test1();\n// Echos Hi\nmyClass::test3();\n// Echos Hi\n$obj = new myClass;\n$obj->test2();