<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    // Sample form data\n    $inputData = $_POST['inputField'];\n\n    // Convert the input data from Windows-1251 to UTF-8\n    $utf8Data = iconv("Windows-1251", "UTF-8//IGNORE", $inputData);\n\n    // Apply htmlspecialchars() to the UTF-8 converted data\n    $safeData = htmlspecialchars($utf8Data, ENT_QUOTES, 'UTF-8');\n\n    // Output the safe data\n    echo $safeData;\n}\n?>\n\n<form method="post" enctype="multipart/form-data">\n    <input type="text" name="inputField">\n    <input type="submit" value="Submit">\n</form>