<?php\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $data = $_POST['data'] ?? '';\n\n    // Sanitize and validate the data as needed\n    $sanitizedData = htmlspecialchars($data, ENT_QUOTES, 'UTF-8');\n\n    // Store the data in a file\n    file_put_contents('data.txt', $sanitizedData . PHP_EOL, FILE_APPEND);\n\n    echo json_encode(['status' => 'success', 'message' => 'Data stored successfully']);\n} else {\n    echo json_encode(['status' => 'error', 'message' => 'Invalid request method']);\n}\n?>